    public void updatetable()
    {
        DefaultTableModel dm = (DefaultTableModel)table_mydb.getModel();
        while(dm.getRowCount() > 0)
        {
            dm.removeRow(0);
        }
        conn=admin_panel.ConnectDb();
        if(conn!=null)
        {
            String sql="SELECT Senders_Name,Senders_Address,Senders_Phone,Receivers_Name,Receivers_Address,Receivers_Phone,Weight,[From],[To],Content,Shipment_Type,Code,Package from order_info";
            try
            { 
                pst=conn.prepareStatement(sql);
                rs=pst.executeQuery();
                Object columnData [] = new Object[13];
                while(rs.next())
                {
                    columnData[0]=rs.getString("Senders_Name");
                    columnData[1]=rs.getString("Senders_Address");
                    columnData[2]=rs.getString("Senders_Phone");
                    columnData[3]=rs.getString("Receivers_Name");
                    columnData[4]=rs.getString("Receivers_Address");
                    columnData[5]=rs.getString("Receivers_Phone");
                    columnData[6]=rs.getString("Weight");
                    columnData[7]=rs.getString("From");
                    columnData[8]=rs.getString("To");
                    columnData[9]=rs.getString("Content");
                    columnData[10]=rs.getString("Shipment_Type");
                    columnData[11]=rs.getString("Code");
                    columnData[12]=rs.getString("Package");
                    model.addRow(columnData);
                    
                }
            }
            catch(Exception e)
            {
                JOptionPane.showMessageDialog(null, e);
            }
        }
    }
    
    public static Connection ConnectDb()
    {
        try
        {
            Class.forName("org.sqlite.JDBC");
            Connection conn = DriverManager.getConnection("jdbc:sqlite:mydb.db");
            System.out.println("connected");
            return conn;
        }
        catch(Exception e)
        {
            JOptionPane.showMessageDialog(null,e);
            return null;
        }
    }
    
    
