    private void order_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_order_btnActionPerformed

         String sql="INSERT INTO order_info(Senders_Name,Senders_Address,Senders_Phone,Receivers_Name,Receivers_Address,Receivers_Phone,Weight,[From],[To],Content,Shipment_Type,Code,Package)VALUES(?,?,?,?,?,?,?,?,?,?,?,?,?)";
        try{
            
            
            
            pst=conn.prepareStatement(sql);
            pst.setString(1, sender_name_f.getText());
            pst.setString(2, sender_address_f.getText());
            pst.setString(3, sender_phone_f.getText());
            pst.setString(4, receiver_name_f.getText());
            pst.setString(5, receiver_address_f.getText());
            pst.setString(6, receiver_phone_f.getText());
            
            Object ac=combo_from.getSelectedItem();
            String opss=ac.toString();
            
            pst.setString(7, opss);
            
            Object acc=combo_to.getSelectedItem();
            String opsss=acc.toString();
            pst.setString(8, opsss);
            
            Object op=content_combo.getSelectedItem();
            String ops=op.toString();
            
            pst.setString(9, ops);
            
            Object ob=ship_combo.getSelectedItem();
            String curr1=ob.toString();
            pst.setString(10, curr1);
            
            Object ob1=weight_combo.getSelectedItem();
            String op1=ob1.toString();
            
            
            pst.setString(11, op1);
            
            
            pst.setString(12, code_f.getText());
            
            JRadioButton selectedButton = null;

        if (combo_basic.isSelected()) {
            selectedButton = combo_basic;
           } 
        else
            selectedButton = combo_gold;
        String choice = selectedButton.getText();
            
            pst.setString(13, choice);
            
            
            g1=sender_name_f.getText();
            g2=sender_address_f.getText();
            g3=sender_phone_f.getText();
            g4=receiver_name_f.getText();
            g5=receiver_address_f.getText();
            g6=receiver_phone_f.getText();
            g7=opss;
            g8=opsss;
            g9=ops;
            g10=curr1;
            g11=op1;
            g12=code_f.getText();
            g13=choice;
            
            
            
            
            if(opsss.equals(opss) || code_f.getText().equals(""))
                JOptionPane.showMessageDialog(null, "An error occurred. Order not placed.\n\n Possible reasons : \n 1. From and To maybe same.\n 2.Code not generated.");
            else
            {
                pst.execute();
                
                JOptionPane.showMessageDialog(null, "Order Placed successfully");
                receipt rcpt=new receipt(g1,g2,g3,g4,g5,g6,g7,g8,g9,g10,g11,g12,g13);
                rcpt.setVisible(true);
                //this.setVisible(false);
            }
            rs.close();
            pst.close();
            
        }
        catch(Exception e)
        {
            JOptionPane.showMessageDialog(null, e);
        }
        updatetable();
        
    }//GEN-LAST:event_order_btnActionPerformed


    private void Print_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Print_btnActionPerformed
        // TODO add your handling code here:
        MessageFormat header = new MessageFormat("Courier Info");
        MessageFormat footer = new MessageFormat("Page {0,number,integer}");
        
        try
        {
            table_mydb.print(JTable.PrintMode.NORMAL, header,footer);
            
        }
        catch(java.awt.print.PrinterException e)
        {
            System.err.format("No Printer Found", e.getMessage());
        }
    }//GEN-LAST:event_Print_btnActionPerformed

    private void gen_btnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_gen_btnActionPerformed
        // TODO add your handling code here:
        try
        {
            String sql="SELECT count(*) from order_info";
            pst=conn.prepareStatement(sql);
            rs=pst.executeQuery();
            if(rs.next())
            {
                String sy=rs.getString("count(*)");
                int i=Integer.parseInt(sy);
                i=i+100;
                sy=Integer.toString(i);
                if(i%2==0)
                code_f.setText("ae478a"+sy+"x12y789z##");
                else
                    code_f.setText("x12y789z##"+sy+"ae478a");
            }
            
        }
        catch(Exception e)
        {
            JOptionPane.showMessageDialog(null, e);
        }
        
    }//GEN-LAST:event_gen_btnActionPerformed

