    private void table_mydbMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_table_mydbMouseClicked
        // TODO add your handling code here:
        try
        {
            int row=table_mydb.getSelectedRow();
            String Table_click=table_mydb.getModel().getValueAt(row,11).toString();
            String sql="select * from order_info where Code = '"+Table_click+"'";
            pst=conn.prepareStatement(sql);
            rs=pst.executeQuery();
            if(rs.next())
            {
                r1=rs.getString("Senders_Name");
                r2=rs.getString("Senders_Address");
                r3=rs.getString("Senders_Phone");
                r4=rs.getString("Receivers_Name");
                r5=rs.getString("Receivers_Address");
                r6=rs.getString("Receivers_Phone");
                r7=rs.getString("Weight");
                r8=rs.getString("From");
                r9=rs.getString("To");
                r10=rs.getString("Content");
                r11=rs.getString("Shipment_Type");
                r12=rs.getString("Code");
                r13=rs.getString("Package");
                
            }
            
        }
        catch(Exception e)
        {
            JOptionPane.showMessageDialog(null, e);
        }
        
        
    }//GEN-LAST:event_table_mydbMouseClicked

